happyNewToken action sts stk [] =
	happyDoAction 7# notHappyAtAll action sts stk []

happyNewToken action sts stk (tk:tks) =
	let cont i = happyDoAction i tk action sts stk tks in
	case tk of {
	CLBracket -> cont 1#;
	CRBracket -> cont 2#;
	CHyphen -> cont 3#;
	CQuestion -> cont 4#;
	CComma -> cont 5#;
	CChar happy_dollar_dollar -> cont 6#;
	_ -> happyError' (tk:tks)
	}

happyError_ tk tks = happyError' (tk:tks)

happyThen :: () => Either ParserError a -> (a -> Either ParserError b) -> Either ParserError b
happyThen = (>>=)
happyReturn :: () => a -> Either ParserError a
happyReturn = (return)
happyThen1 m k tks = (>>=) m (\a -> k a tks)
happyReturn1 :: () => a -> b -> Either ParserError a
happyReturn1 = \a tks -> (return) a
happyError' :: () => [(Character)] -> Either ParserError a
happyError' = parseError

parse tks = happySomeParser where
  happySomeParser = happyThen (happyParse 0# tks) (\x -> happyReturn (happyOut4 x))

happySeq = happyDontSeq


