-- parseError _ = error "parse error"
-- parseError _ = Nothing

parseError tokens = Left (ParserError tokens)

instance Monad (Either ParserError) where
  return v = Right v
  (Left s) >>= _ = Left s
  (Right r) >>= f = f r

data ParserError = ParserError [Character] deriving (Show)
